import { Component } from '@angular/core';
import { UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'a11y-p1';
  // public yesNoAnswer = 'no';
  public formGroup: UntypedFormGroup = null;

  constructor(formBuilder: UntypedFormBuilder) {
    this.formGroup = formBuilder.group({
      yesNoAnswer: [{
        value: 'no',
        disabled: false
      }]
    });
  }

  public submit(): void {
    this.formGroup.get('yesNoAnswer').disable();
    console.log(this.formGroup.value, 'ibag');
  }
}
<main>
  <h1 tabindex="0">Form</h1>
  <form [formGroup]="formGroup" (ngSubmit)="submit()">
    <!--
      formControlName: Esta diretiva é utilizada para ligar um elemento de
      entrada do HTML a um Control de um FormGroup.
    -->
    <app-yes-no-button-group
      [appDisabedControl]="true"
      formControlName="yesNoAnswer"
      label="Are you good enough in Angular"
    ></app-yes-no-button-group>
  </form>
  <!-- (valueChange)="yesNoAnswer = $event" -->
  <!-- <span>yesNoAnswer value: {{ yesNoAnswer }}</span> -->
  <button>Submit</button>
</main>
